#
# Make a clean directory structure for the tifs generated by 
#    run_compute_exceedance_rates_for_threshold_depth_logic_tree_mean_newParallelPartition.sh
# AND 
#    all the *.sh scripts generated by `make_exceedance_rate_jobs.R` (epistemic uncertainty calcs over multiple nodes)
#

# This should match the beginning of the folders that will be moved to better locations
runname='sea_level_vary'
output_dir='ptha_tidal_check/'${runname}

for variable in depth max_stage max_speed; do
  # Move logic-tree-mean hazard & variance rasters to a clean location
  mkdir -p ${output_dir}/highres_${variable}_with_variance ;
  mv ${runname}-${variable}-LogicTreeMean* ${output_dir}/highres_${variable}_with_variance;
  
  # Move epistemic uncertainty rasters to a clean location
  # 16th percentile
  mkdir -p ${output_dir}/highres_${variable}_epistemic_uncertainty/16pc; 
  mv ${runname}-${variable}_exrate_*_0.16_* ${output_dir}/highres_${variable}_epistemic_uncertainty/16pc/;
  # 84th percentile
  mkdir -p ${output_dir}/highres_${variable}_epistemic_uncertainty/84pc;
  mv ${runname}-${variable}_exrate_*_0.84_* ${output_dir}/highres_${variable}_epistemic_uncertainty/84pc/;
done
